import { Fragment, FunctionComponent, useEffect, useState } from "react";
import { RadioButton } from "../component";
import { NavBar } from "../component/NavBar";

export interface ICheckoutProps{};

export const Checkout: FunctionComponent<ICheckoutProps> = (props) => {


// const handleOnChange = (event: any) =>{
// console.log(event.target.value)
// }
const [productOne,setProductOne] = useState<number>(3488.99)
const [total, setTotal]= useState<number>(0);
const [koneCount, setKoneCount] = useState(0);
const [checkZero, setCheckZero] = useState<boolean>(false);
const incrementCount = () => {
  setKoneCount(koneCount + 1);
}
const decrementCount = () => {
  setKoneCount(koneCount - 1); 
}
useEffect (()=>{
  if(koneCount <= 0){
    setCheckZero(true);
  }else{
    setCheckZero(false);
  }
}, [koneCount])

return(
  <Fragment>
     <NavBar />
    <div style={{paddingLeft:"50px"}}>
      <h1>This is the checkout page</h1>
      <div>
        <label>Level</label>
        <div className="flex_row">
          <RadioButton value="5%" name="level" label="Associate" />
          <RadioButton value="30%" name="level" label="Diamond" />
        </div>

       <div style={{marginTop: "10px"}}>
          <div className="flex_row">
            <label style={{marginRight:"20px"}}>Kone</label>
            <button onClick={incrementCount}>+</button>
            <div style={{marginLeft: "15px", marginRight:"15px"}}>{koneCount}</div>
            <button onClick={decrementCount} disabled={checkZero}>-</button>
          </div>

          <div className="flex_row">
            <label style={{marginRight:"20px"}}>IronhideCatride</label>
            <button onClick={incrementCount}>+</button>
            <div style={{marginLeft: "15px", marginRight:"15px"}}>{koneCount}</div>
            <button onClick={decrementCount} disabled={checkZero}>-</button>
          </div>

          <div>{total}</div>

        </div> 

      </div>

    </div>
  </Fragment>
)
}
