import { Fragment, FunctionComponent, useEffect, useState } from "react";
import { RadioButton } from "../component";
import { NavBar } from "../component/NavBar";

export interface ICheckoutProps{};

export const Checkout: FunctionComponent<ICheckoutProps> = (props) => {
const [level, setLevel] = useState("");

const handleOnChange = (event: any) =>{
setLevel(event.target.value);
}

const [productOne,setProductOne] = useState<number>(3488.99)
const [total, setTotal]= useState<number>(0);
const [checkProdOne, setCheckProdOne] = useState<boolean>(false);
const [checkProdTwo, setCheckProdTwo] = useState<boolean>(false);
const [prodOneCount, setProdOneCount] = useState<number>(0);

const incrementOneCount = () => {
  calculate();
  setProdOneCount(prodOneCount + 1);

}
const decrementOneCount = () => {
  calculate();
  setProdOneCount(prodOneCount - 1); 

}
const [prodTwoCount, setProdTwoCount] = useState(0);

const incrementTwoCount = () => {
  calculate();
  setProdTwoCount(prodTwoCount + 1);

}
const decrementTwoCount = () => {
  calculate();
  setProdTwoCount(prodTwoCount - 1); 

}

// listener 
useEffect (()=>{
  if(prodOneCount <= 0){
    setCheckProdOne(true);
  }else{
    setCheckProdOne(false);
  }
  if(prodTwoCount <= 0){
    setCheckProdTwo(true);
  }else{
    setCheckProdTwo(false);
  }
if(prodOneCount>2){
  setProductOne(2588.99)
}

}, [prodOneCount, prodTwoCount])

// the logic 
// TODO 
// if client is diamond include 20% discount. if purchase above 3, remove discount. change price. - KONE 
// else client include 5% discount. 
//calculate kone and catrigde individually, return value and add to total. 

const calculate = () => {
if(level === "diamond"){
  let calculate = prodOneCount * productOne;
  let calc = calculate.toFixed(2)
  setTotal(parseFloat(calc));
}
}

return(
  <Fragment>
     <NavBar />
    <div style={{paddingLeft:"50px"}}>
      <h1>This is the checkout page</h1>
      <div>
        <label>Level</label>
        <div className="flex_row" onChange={handleOnChange}>
          <RadioButton value="associate" name="level" label="Associate" />
          <RadioButton value="diamond" name="level" label="Diamond" />
        </div>

       <div style={{marginTop: "10px"}} >

          <div className="flex_row">
            <label style={{marginRight:"20px"}}>Kone</label>
            <button onClick={incrementOneCount}>+</button>
            <div style={{marginLeft: "15px", marginRight:"15px"}} onChange={calculate}>{prodOneCount}</div>
            <button onClick={decrementOneCount} disabled={checkProdOne}>-</button>
          </div>

          <div className="flex_row">
            <label style={{marginRight:"20px"}}>IronhideCatride</label>
            <button onClick={incrementTwoCount}>+</button>
            <div style={{marginLeft: "15px", marginRight:"15px"}} onChange={calculate}>{prodTwoCount}</div>
            <button onClick={decrementTwoCount} disabled={checkProdTwo}>-</button>
          </div>

        </div> 
        {/* total  */}
        <div className="flex_row">
            <label>Total:</label>
            <div>{total}</div>
          </div>
      </div>

    </div>
  </Fragment>
)
}
