import { Fragment, FunctionComponent, useState } from "react";
import { RadioButton } from "../component";
import { NavBar } from "../component/NavBar";

export interface ICheckoutProps{};

export const Checkout: FunctionComponent<ICheckoutProps> = (props) => {


const handleOnChange = (event: any) =>{
console.log(event.target.value)
}
let [count, setCount] = useState(0);
function incrementCount() {
  count = count + 1;
  setCount(count);
}
function decrementCount() {
  count = count - 1;
  setCount(count);
}
return(
  <Fragment>
     <NavBar />
    <div>
      <h1>This is the checkout page</h1>
      <div>
        <label>Level</label>
        <div className="flex_row" onChange={handleOnChange}>
          <RadioButton value="5%" name="level" label="Associate" />
          <RadioButton value="30%" name="level" label="Diamond" />
        </div>

       <div style={{marginTop: "10px"}}>
          <div className="flex_row">
          <button onClick={incrementCount}>+</button>
          <div>{count}</div>
          <button onClick={decrementCount}>-</button>
          </div>

        </div> 

      </div>

    </div>
  </Fragment>
)
}
